package variables;

public class TiposdeVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		byte edadAlumno = 17; //Declaración e Inicialización
		
		byte edadProfesor = 0; //Declaración e Inicialización
		
		double alturaAlumno = 1.73, pesoAlumno = 63.5; //Declaración e Inicialización de peso y altura
		
		char letraDNI = 'A';//Declaración e Inicialización
		
		String nombreAlumno = "Camilo";//Declaración e Inicialización
		
		boolean mayorEdad = false;
		
		
		
		
		System.out.println("Nombre del alumno: " + nombreAlumno);
		
		System.out.println("La edad del alumno: " + edadAlumno);
		
		System.out.println("El alumno es mayor de edad = " + mayorEdad );
		
		System.out.println("El peso del alumno es de " + pesoAlumno+"g" + "\nLa altura es de " + alturaAlumno + "m" );
		
		System.out.println("La letra de su DNI es: " + letraDNI);
		
	
		
		
		
	}
	

}
